//USING A JAPENEESE WEBSITE TO MAKE A EXTENSION

const blockIconURI = 'data:image/png;base64,';

class RoomBox {



  getInfo() { // 拡張機能の各種情報
    return {
      id: 'roombox',
      name: 'RoomBox', // The name of the extension
      blockIconURI: blockIconURI,
      showStatusButton: false,
      blocks: [ // 各ブロックの定義
        {
          opcode: 'hello', // このブロックが実行されると、helloという関数が呼ばれる
          blockType: Scratch.BlockType.COMMAND,　// tell scratch to know that ts a block
          text: 'hello' // ブロックに表示されるテキスト
        }
      ]
    }
  }

  hello() {
    console.log('hello'); // console log に hello と出力
  }
}

Scratch.extensions.register(new RoomBox());
